function checkDateDisabled() {
    const prevButton = document.querySelector('.xdsoft_prev');
    const nextButton = document.querySelector('.xdsoft_next');
    const currentElement = document.querySelector('.xdsoft_date.xdsoft_current');
    const datePickerInput = document.querySelector('.form-control.datetimepicker');
    const maxOffsetDays = datePickerInput.getAttribute('data-max-offset-days');
    const currentDate = parseInt(currentElement.getAttribute('data-date'), 10);
    const currentMonth = parseInt(currentElement.getAttribute('data-month'), 10);
    const currentYear = parseInt(currentElement.getAttribute('data-year'), 10);
    const lastDayOfMonth = new Date(currentYear, currentMonth, 0);
    const daysCount = getDaysCount(new Date(currentYear, currentMonth - 1, currentDate), lastDayOfMonth);
    const monthScrollBox = document.querySelector('.xdsoft_select.xdsoft_monthselect.xdsoft_scroller_box');
    const yearScrollBox = document.querySelector('.xdsoft_select.xdsoft_yearselect.xdsoft_scroller_box');

    function getDaysCount(startDate, endDate) {
        const oneDay = 24 * 60 * 60 * 1000; 
        return Math.round((endDate - startDate) / oneDay);
    }

    if (currentElement) {
        if (currentElement.classList.contains('xdSoftDisabled')) {
            const timeBox = document.querySelector('.xdsoft_time_box.xdsoft_scroller_box');
            
            if (timeBox) {
                timeBox.style.display = 'none';
            }
        }

        if (daysCount < maxOffsetDays) {
            nextButton.style.display = 'block';            
        } else {
            nextButton.style.display = 'none';
            if (monthScrollBox && yearScrollBox) {
                monthScrollBox.remove();
                yearScrollBox.remove();
            }
        }
        prevButton.style.display = 'none';

        nextButton.addEventListener('click', function () {
            nextButton.style.display = 'none';
            prevButton.style.display = 'block';
            const calendarElement = document.querySelector('.xdsoft_calendar');
            const disabledElements = calendarElement.querySelectorAll('.xdsoft_disabled');
            toggleTimepicker();
            disabledElements.forEach((element) => {
                if (element.classList.contains('xdsoft_current')) {
                    element.classList.remove('xdsoft_current');
                }
            });
        });

        prevButton.addEventListener('click', function () {
            prevButton.style.display = 'none';
            nextButton.style.display = 'block';
            const calendarElement = document.querySelector('.xdsoft_calendar');
            const disabledElements = calendarElement.querySelectorAll('.xdsoft_disabled');
            toggleTimepicker();
            disabledElements.forEach((element) => {
                if (element.classList.contains('xdsoft_current')) {
                    element.classList.remove('xdsoft_current');
                }
            });
        });
    }
}

function toggleTimepicker() {
    const calendarElement = document.querySelector('.xdsoft_calendar');
    const dateElements = calendarElement.querySelectorAll('.xdsoft_date');
    const timepickerElement = document.querySelector('.xdsoft_timepicker');

    timepickerElement.style.display = "none";

    dateElements.forEach((dateElement) => {
        dateElement.addEventListener('click', () => {
            if (!dateElement.classList.contains('xdsoft_disabled')) {
                timepickerElement.style.display = "block"
            }
        });
    });
};
